	toggleEngageFlows: function(direction) {

		var oldIndex = appcuesFlywheel.flowIndex;
		
		if((appcuesFlywheel.flowIndex + direction) >= $('.fw-engage-item').length) {
			appcuesFlywheel.flowIndex = 0;
		} else if ((appcuesFlywheel.flowIndex + direction) < 0) {
			appcuesFlywheel.flowIndex = ($('.fw-engage-item').length - 1);
		} else {
			appcuesFlywheel.flowIndex = appcuesFlywheel.flowIndex + direction;	
		}

		//debugger;

		var currentlyActiveFlow = '[data-flow-position="' + oldIndex + '"]';
		var newActiveFlow = '[data-flow-position="' + appcuesFlywheel.flowIndex + '"]';

		var currentlyActiveDot = '[data-dot-position="' + oldIndex + '"]';
		var newActiveDot = '[data-dot-position="' + appcuesFlywheel.flowIndex + '"]';



		$('#fw-stage-engage').find(currentlyActiveFlow).removeClass("active");
		$('#fw-stage-engage').find(newActiveFlow).addClass("active");

		$("#fw-engage-dots").find(currentlyActiveDot)[0].src = "https://public.appcues.com/microsites/flywheel/img/icons/dot-inactive.svg";
		$("#fw-engage-dots").find(newActiveDot)[0].src = "https://public.appcues.com/microsites/flywheel/img/icons/dot-active.svg";

	},

	toggleResourceContent: function(direction) {

		var oldIndex = appcuesFlywheel.resourceIndex;
		
		if((appcuesFlywheel.resourceIndex + direction) >= $('.fw-resource-item').length) {
			appcuesFlywheel.resourceIndex = 0;
		} else if ((appcuesFlywheel.resourceIndex + direction) < 0) {
			appcuesFlywheel.resourceIndex = ($('.fw-resource-item').length - 1);
		} else {
			appcuesFlywheel.resourceIndex = appcuesFlywheel.resourceIndex + direction;	
		}

		var currentlyActiveResource = '[data-resource-position="' + oldIndex + '"]';
		var newActiveResource = '[data-resource-position="' + appcuesFlywheel.resourceIndex + '"]';

		var currentlyActiveDot = '[data-dot-position="' + oldIndex + '"]';
		var newActiveDot = '[data-dot-position="' + appcuesFlywheel.resourceIndex + '"]';

		$('#fw-action-resources').find(currentlyActiveResource).removeClass("active");
		$('#fw-action-resources').find(newActiveResource).addClass("active");

		$("#fw-resource-dots").find(currentlyActiveDot)[0].src = "https://public.appcues.com/microsites/flywheel/img/icons/dot-inactive.svg";
		$("#fw-resource-dots").find(newActiveDot)[0].src = "https://public.appcues.com/microsites/flywheel/img/icons/dot-active.svg";

	},

